mapper.select:
select id, name ,description, sort, img_url imgUrl, is_display isDisplay from bbs_brand
name like "%"#{name}"%"
and is_display = #{isDisplay}
limit #{startRow}, #{pageSize}

select count(1) from bbs_brand
name like "%"#{name}"%"
and is_display = #{isDisplay}

mapper.update:
update bbs_brand
name = #{name}, description = #{description}, sort = #{sort}, img_url = #{imgUrl}, is_display = #{isDisplay}
id = #{id}
